void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u = fragCoord/iResolution.xy;
    float a = texture(iChannel0,u).z;
          a = pow(a,.1);
    fragColor = cos(a*4.+vec4(1,2,3,4)+1.)*.6+.4;
}

//extracts divergence around each pixel
#define A(u) texture(iChannel0,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    float p = 0.;
    //kernel convolution
    {
        float z    = 8.;//kernel convolution size
        float blur = 2./z;
        for(float i=-z; i<=z; ++i){
        for(float j=-z; j<=z; ++j){
            vec2  c  = vec2(i,j)*blur; //c = c.yx*vec2(-1,1);
            vec4  t2 = A(u+vec2(i,j));
            p += dot(t2.xy,c)/exp(dot(c,c));
        }}
    }
    fragColor = vec4(p);
}

//use the amount of divergence calculated in BufferA to enhance the divergence field
#define A(u) texture(iChannel0,(u)/iResolution.xy)
#define B(u) texture(iChannel1,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    vec4 a = A(u);//A(u)*.9  vec4(0)
    //kernel convolution
    {
        float z    = 4.;//kernel convolution size
        float blur = 2./z;
        for(float i=-z; i<=z; ++i){
        for(float j=-z; j<=z; ++j){
          float p = B(u+vec2(i,j)).x;
          vec2  c = -vec2(i,j)*blur; //c = c.yx*vec2(-1,1);
          a.xy += c*p/exp(dot(c,c))*.003;
        }}
    }
    fragColor = a;
}

//calculate how much each pixel is going to move and how to normalize it
#define A(u) texture(iChannel0,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    float s  = 0.;
    //kernel convolution
    {
        vec4  t  = A(u);
        vec2  mv = t.xy;
        float dl = sin(t.z*3.)*2.+4.; //dl = min(dl,5.);
        float z    = 6.;//kernel convolution size
        float blur = 1./z;
        for(float i=-z; i<=z; ++i){
        for(float j=-z; j<=z; ++j){
            vec2 c = (mv+vec2(i,j))*blur*dl;
            s += 1./exp(dot(c,c));
        }}
        if(s==0.){s = 1.;}
    }
    fragColor = vec4(s);
}

//read the translation and normalization calculated in Buffer C and apply it
#define A(u) texture(iChannel0,(u)/iResolution.xy)
#define B(u) texture(iChannel1,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    vec4  a = vec4(0);
    //kernel convolution
    {
        float z    = 6.;//kernel convolution size
        float blur = 1./z;
        for(float i = -z; i<=z;++i){
        for(float j = -z; j<=z;++j){
          vec4  t  = A(u+vec2(i,j));
          float dl = sin(t.z*3.)*2.+4.; //dl = min(dl,5.);
          vec2  mv = t.xy;
          float s = B(u+vec2(i,j)).x;
          vec2 c = (mv-vec2(i,j))*blur*dl;
          a += t/s/exp(dot(c,c));
        }}
    }
    if(iFrame==0)
    {
        vec2 v = 1.*(2.*u-iResolution.xy)/iResolution.y;
        a = .1/exp(dot(v,v)) + vec4(0,0,1,1)*.0;
    }
    fragColor = a;
}
