vec3 dither(vec3 raw, vec2 pos) {
    const float steps = 3.;
    //return floor(raw * steps + 0.25 * (mod(pos.x+pos.y+1., 2.) + 0.5*mod(pos.x, 2.)))/ steps;
    return (floor(raw * steps) + step(0.5*fract(69.1*sin(pos.x*4.8 + 7.96*cos(pos.y*2.9))), fract(raw * steps)))/steps;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 position = (fragCoord.xy * 2. - iResolution.xy) / length(iResolution.xy);

    float cX = position.x;
    float cY = position.y;

    float newX = log(length(position));
    float newY = atan(cY, cX);

	float PI = 3.14159;
	float numHorBands = 6.0;
	float numVertBands = 12.0;
	float numDiagBands = 10.0;
    float numArms = 4.0;
	float numLines = 5.0;
	float numRings = 3.0;
	float spiralAngle = -PI/3.0;

    float color = 0.0;

    float time = fract(iTime / 4.) * 2.;

	//Vertical Bands
	color += 0.3 * cos(numVertBands*cY + time * PI * 2.);	
	//Horizontal Bands
	color += 0.4 * cos(numHorBands*cX - time * PI * 2.);	
	//Diagonal Bands
	color += 0.1* cos(2.0*numDiagBands*(cX*sin(spiralAngle) + cY*cos(spiralAngle)) + time * PI * 2.);	
	//Arms
	//color += cos(numLines*newY + iTime);
	//Rings
	color += cos(numRings*newX + time * PI * 3.);
    //Spirals
	color += cos(2.0*numArms*(newX*sin(spiralAngle) + newY*cos(spiralAngle)) + time * PI);
	//overall brightness/color
	//color *= cos(iTime/10.0);
    fragColor = vec4( 0.5 + dither(
    0.5 * vec3( sin( color + time * PI ) * 0.75, sin(0.6 + color + time * PI * 2.), sin( 1.3 + color + time * PI ) * 0.75 )
    , fragCoord.xy), 1.0 );
    //fragColor=vec4(dither(vec3(color),fragCoord.xy),1.0);

}
