void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float customUvX = fract(uv.x + tan(iTime+uv.y*3.0) * 0.01);
    float customUvY = fract(uv.y + tan(iTime+uv.x*10.0) * 0.01);
    fragColor = texture(iChannel0,vec2(customUvX, customUvY));
}
