#define H(v) fract(1e4*sin((v)*mat2(R,R-71.)))
#define S(v) smoothstep(30./R.y,0.,v)

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy, P,D,_P,
          U = 10.* ( u+u - R ) / R.y, U0=U;
    float m = 9.,d, t = iTime;

    for ( int k=0; k < 9; k++ )
        D = vec2(k%3,k/3), P = D - H(floor(U)+D),
        d = length(P-fract(U)), 
        d < m ? _P=P, m = d : m;
                                                          // draw disk - dark disk    
    O = vec4( clamp( S(m-.4) - S( length(_P-fract(U)+ U*length(U)/2e3 ) -.4 )  ,0.,1.) );
}
