#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
#define N(p,n)pow(dot(pow(abs(p),vec2(n)),vec2(1)),1./n)
#define L(p)N(p,8.)
//#define L(p)max(abs(p.x),abs(p.y)) 
//#define L(p)length(p)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,d=normalize(vec3((C-.5*r.xy)/r.y,1.));
    for(float i=0.,g=0.,e,t=iTime;++i<99.;){
        p=g*d;
        p.z-=4.;
        p=R(p,vec3(.577),t);
        vec4 q=vec4(p,0);
        g+=e=(length(vec2(vec4(L(q.xy),L(q.yz),L(q.zw),L(q.wx))-1.))-.2)*.7;
        O.xyz+=mix(vec3(1),H(atan(q.z,q.x)*atan(1.)*2.),.9)*.012/exp(i*i*e);
    }
}
