#define _Speed 0.302
#define _Fading 1.0
#define _LuminanceJitterThreshold 0.895

float randomNoise(vec2 c)
{
    return fract(sin(dot(c.xy, vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 sceneColor = texture(iChannel0, uv);
    vec4 noiseColor = sceneColor;

    float luminance = dot(noiseColor.rgb, vec3(0.22, 0.707, 0.071));
    if (randomNoise(vec2(iTime * _Speed, iTime * _Speed)) > _LuminanceJitterThreshold)
	{
		noiseColor = vec4(luminance, luminance, luminance, luminance);
	}

	float noiseX = randomNoise(iTime * _Speed + uv / vec2(-213, 5.53));
	float noiseY = randomNoise(iTime * _Speed - uv / vec2(213, -5.53));
	float noiseZ = randomNoise(iTime * _Speed + uv / vec2(213, 5.53));

	noiseColor.rgb += 0.25 * vec3(noiseX,noiseY,noiseZ) - 0.125;


    // Output to screen
    fragColor = mix(sceneColor, noiseColor, _Fading);
}
