void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = texture(iChannel0, uv);
}

#define ScreenCount 3.             //Increase for more complex shapes
#define ColorDelta .00001          //Decrease for less static

#define pi 3.14159

float Rectangle(vec2 uv, vec2 Size, vec2 Pos, float Blur) {
    uv /= Size;
    uv -= Pos;

    float XBand = smoothstep(-Size.x - Blur, -Size.x + Blur, uv.x) * smoothstep(Size.x + Blur, Size.x - Blur, uv.x);
    float YBand = smoothstep(-Size.y - Blur, -Size.y + Blur, uv.y) * smoothstep(Size.y + Blur, Size.y - Blur, uv.y);
    return XBand * YBand;
}

vec3 GetColor(float In)
{
    In += iTime * .075;
    In *= pi * 2.;
    return vec3(cos(In + pi * .333333) * .5 + .5, cos(In + pi * .666666) * .5 + .5, -cos(In + pi) * .5 + .5);
}

float Screen(vec2 uv, float Rot, vec2 Size, vec2 Pos)
{
    uv /= Size;
   	uv *= mat2(cos(Rot), -sin(Rot), sin(Rot), cos(Rot));
    uv -= Pos;

    if (Rectangle(uv, vec2(.7), vec2(0.), .0001) == 0.) {
        return .0075;
    }
    uv = (uv * iResolution.y + .5 * iResolution.xy) / iResolution.xy;

    return fract((uv.x + uv.y) * ColorDelta + texture(iChannel0, uv).w - ColorDelta);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;

    float Sum = 0.;
    for (float i = 0.; i < ScreenCount; i++) {
        float t = cos(float(i) / ScreenCount * pi * cos(iTime * pi * .1) * 2.);
        Sum = Screen(uv, iTime, vec2(cos(t) * .1 + .5), vec2(cos(t * 2.) * .3, sin(t * 2.) * .3)) + Sum;
    }

    fragColor = vec4(GetColor(Sum), Sum);
}
