#define rot(a) mat2( cos(a),-sin(a),sin(a),cos(a) )

float inv(float a) {return a - a * 2.; }

vec3 inv(vec3 a) {return vec3(a.x - a.x * 2., a.y - a.y * 2., a.z - a.z * 2.);}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    uv *= rot(0.5 * iTime);

    uv = fract(uv * 2.) -0.5;

    uv *= rot(-1. * iTime);

    float xdis = sin(uv.y * 15. * sin(iTime * 3.)) / 6.;
    float ydis = sin(uv.x * 15. * sin(iTime * 3.)) / 6.;

    uv += vec2(xdis,ydis);

    float bright = 0.;

    bright = step(0., (length(uv) - 0.3) * step(0., (abs(uv.x) - 0.1) + (abs(uv.y) - 0.1)));

    float r = 1. - (smoothstep(0.,1., length(uv) * 5.));    
    if(abs(uv.x) < 0.2 && abs(uv.y) < 0.2) bright = inv(bright);
    r *= inv(bright);
    bright = inv(bright);

    // Output to screen
    fragColor = vec4(bright, r , 0, 1.);
}
