void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


float d = (uv.x - 0.5) * (uv.x - 0.5) + (uv.y - 0.5) * (uv.y-0.5);
//d*=2.;
float v= cos(0.02 * iTime) *(uv.y-0.5) / (uv.x - 0.5);
float theta = atan(v + 1. * 3.14159 * (1. + cos(v+0.51598374513 * iTime) + cos(v - 0.12482456 * iTime)));

float val = cos(1. *theta + floor(theta * 0.5 * (1. + cos(0.02 * iTime))) * iTime);

    // Time varying pixel color
   // vec3 col = 0.5 * vec3(1. + sin(theta) *cos( theta + iTime));
vec3 col = vec3(1. - smoothstep(val, d,0.15 + 0.05 * cos(0.1 * iTime)),
1. - smoothstep(val, 0.5 * d,0.15 + 0.05 * cos(0.1 * iTime + 2. * 3.14159 / 3.)),
1. - smoothstep(val, 0.25  * d,0.15 + 0.05 * cos(0.1 * iTime + 4. * 3.14159 / 3.))
);

    // Output to screen
    fragColor = vec4(col,1.0);

}
