void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = asin(sin(iTime*0.5)*.9)*2.2;
    //t = iTime*0.9;
    vec2 R = iResolution.xy;
    vec2 ruv = (fragCoord-.5*R.xy)/R.y;
    vec3 col = vec3(0);

    for(float i = 0.0; i<3.0;i++){
        vec2 uv = ruv;
        t+=0.125;
        uv.y+=0.5;
        uv.y*=abs(uv.x*0.1)+0.8;
        uv*=0.5;
        uv.x*=1.2;
        uv.y+=abs(uv.x*uv.x);
        uv=-uv.yx; 
        uv.y+=(uv.x*3.)*sin(sin(t*10.+uv.x*25.))/10.;
        uv.x-=asin(sin(t+uv.y*30.))/20.;
        vec2 d = abs(uv);
        col.r += 0.24*(step(max(d.x,d.y),0.3)) + 0.2*(step(max(d.x,d.y),0.2));
        col.g += 0.33*(step(max(d.x,d.y),0.2));
        col.b += 0.33*(step(max(d.x,d.y),0.1));
    }
    col += 0.2*vec3(0.490,0.192,0.196);
    fragColor = vec4(col,1.0);
}
