float metaball(vec2 p,vec2 uv){

   return 1.0/((uv.x-p.x)*(uv.x-p.x)+(uv.y-p.y)*(uv.y-p.y));
}

float noiseRand( vec2 co){
     return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

# define COUNT 100
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float w=iResolution.x/iResolution.y;
    vec2 uv = fragCoord/iResolution.xy;
    float PI=3.1415926;
    vec2 uv1=(uv-0.5)*10.0;
    uv1.x*=w;

    float d=0.0;
    for(int i=0;i<COUNT;i++){
       float a=float(i)*2.0*PI/float(COUNT);
       a+=noiseRand(vec2(0.0,float(i)));
       vec2 v=vec2(cos(a),sin(a))*sin(iTime*0.2+mod(float(i),3.0))*5.0;
       d+=metaball(v,uv1);
    }


    if(d>=20.0){
      fragColor=vec4(d/100.0);
    }else{
      fragColor=vec4(0.0,0.0,0.0,1.0);
    }
}
