void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - iResolution.xy * 0.5) + 1000.0*vec2(sin(iTime*0.54), cos(iTime*0.45));
    float base = 2.0;
    float digit = 10.0 + sin(iTime*0.127) * 3.0;
    fragColor = vec4((mod((floor(uv.x) * floor(uv.y) / pow(base, digit)), 1.0)));
    // fragColor = vec4(((int(uv.x*uv.y)>>((iFrame/60%7)+5))&1));
}
