void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0);

    // Load texture from webcam
    col = texture(iChannel0, uv).rgb;

    // Convert texture to monocrome
    col = vec3((col.r + col.g + col.b)/3.);

    // Set colors:    
    vec3 kv = vec3(0.0, 0.36, 0.73);
    vec3 lv = vec3(1.0, 0.83, 0.0);

    // Adjust contrast/treashold:
    col = smoothstep(0.45, 0.55, col);

    // Split and flip the colors:
    if (uv.y < 0.5 ) { col = smoothstep(1.0, 0.5, col); }
                else { col = smoothstep(0.5, 1.0, col); }

    // mix colors:
    col = mix(kv, lv, col);    

    fragColor = vec4(col,1.0);
}
