void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u_dir = vec2 (0.0, 1.0);
    vec2 uv = fragCoord/iResolution.xy;

    vec4 color = vec4(0.0);

    //apply blurring, using a 9-tap filter with predefined gaussian weights
	color += texture(iChannel0, vec2(uv.x - 0.00625  * u_dir.x, uv.y - 0.01111111111 * u_dir.y)) * 0.0162162162;
	color += texture(iChannel0, vec2(uv.x - 0.0046875  * u_dir.x, uv.y - 0.00833333333 * u_dir.y)) * 0.0540540541;
	color += texture(iChannel0, vec2(uv.x - 0.003125  * u_dir.x, uv.y - 0.00555555555 * u_dir.y)) * 0.1216216216;
	color += texture(iChannel0, vec2(uv.x - 0.0015625  * u_dir.x, uv.y - 0.00277777777 * u_dir.y)) * 0.1945945946;

	color += texture(iChannel0, uv) * 0.2270270270;

	color += texture(iChannel0, vec2(uv.x + 0.0015625  * u_dir.x, uv.y + 0.00277777777 * u_dir.y)) * 0.1945945946;
	color += texture(iChannel0, vec2(uv.x + 0.003125  * u_dir.x, uv.y + 0.00555555555 * u_dir.y)) * 0.1216216216;
	color += texture(iChannel0, vec2(uv.x + 0.0046875  * u_dir.x, uv.y + 0.00833333333 * u_dir.y)) * 0.0540540541;
	color += texture(iChannel0, vec2(uv.x + 0.00625  * u_dir.x, uv.y + 0.01111111111 * u_dir.y)) * 0.0162162162;

    // Would be better with a FBO

    u_dir = vec2(1.0, 0.0);

	color += texture(iChannel0, vec2(uv.x - 0.00625  * u_dir.x, uv.y - 0.01111111111 * u_dir.y)) * 0.0162162162;
	color += texture(iChannel0, vec2(uv.x - 0.0046875  * u_dir.x, uv.y - 0.00833333333 * u_dir.y)) * 0.0540540541;
	color += texture(iChannel0, vec2(uv.x - 0.003125  * u_dir.x, uv.y - 0.00555555555 * u_dir.y)) * 0.1216216216;
	color += texture(iChannel0, vec2(uv.x - 0.0015625  * u_dir.x, uv.y - 0.00277777777 * u_dir.y)) * 0.1945945946;

	color += texture(iChannel0, uv) * 0.2270270270;

	color += texture(iChannel0, vec2(uv.x + 0.0015625  * u_dir.x, uv.y + 0.00277777777 * u_dir.y)) * 0.1945945946;
	color += texture(iChannel0, vec2(uv.x + 0.003125  * u_dir.x, uv.y + 0.00555555555 * u_dir.y)) * 0.1216216216;
	color += texture(iChannel0, vec2(uv.x + 0.0046875  * u_dir.x, uv.y + 0.00833333333 * u_dir.y)) * 0.0540540541;
	color += texture(iChannel0, vec2(uv.x + 0.00625  * u_dir.x, uv.y + 0.01111111111 * u_dir.y)) * 0.0162162162;


    fragColor = color * 0.5;
}
