# define BLUR .75 / iResolution.y

float Circle(vec2 uv, vec2 pos) {
    return smoothstep(0.2 + BLUR, 0.2-BLUR, length(uv-pos));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float sun = Circle(uv, vec2(0.5, -0.3));
    // smoothstep(-.3, -.3 +.02, uv.y)
    float sky = smoothstep(-.3, -.28, uv.y);

    vec3 seaBlue = vec3(0., 0., 1.);
    vec3 skyBlue = vec3(0., .5, 1.);

    //vec3 col = mix(seaBlue, skyBlue, sky);
    //col = mix(col, vec3(1., 1., 0.), sun);

    // ordering matters because I'm drawing a full circle
    vec3 col = mix(skyBlue, vec3(1.,1.,0.), sun);
    col = mix(seaBlue, col, sky);

    // Output to screen
    fragColor = vec4(col,1.0);
}
