float Hash21(vec2 p)
{
    p = fract(p*vec2(234.34, 435.345));
    p+= dot(p, p+34.23);
    return fract(p.x*p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord.xy * 2. - iResolution.xy) / min(iResolution.x, iResolution.y);
	vec2 gv = uv * 15.;
    vec2 fr = fract(gv) - .5;
    vec2 fl = floor(gv);
    vec3 col;
    float noise;
    float n;
    vec2 v;
    vec3 colp;
    n = Hash21(vec2(fl + (floor(iTime) * .2)));
    if(n <= .25)
    {
        v = vec2(1, 1);
        colp = vec3(.55, .53, .8);
    }
    else if(n > .25 && n <= .5)
    {
        v = vec2(1, -1);
        colp = vec3(.2382, .1718, .5507);
    }
    else if(n > .5 && n <= .75)
    {
        v = vec2(-1, 1);
        colp = vec3(.5664, .4179, .7460);
    }
    else
    {
        v = vec2(-1, -1);
        colp = vec3(.7851, .5859, .7968);
    }

    noise = Hash21(vec2(fl + (floor(iTime) * .1)));

    float an = abs((noise * 2. - 1.)/2.);
    float c = smoothstep(-.01, .01, length(vec2(fr.x + (an * v.x), fr.y + (an * v.y))) - (.5 - an));
    if(c == .0)
    {
        col.rgb += colp;
    }
    else
    {
        col = vec3(.109, .046, .355);
    }

    if(fr.x > .48 || fr.y > .48)
    {
        //col = vec3(1., .0, .0);
    }

    fragColor = vec4(col, 1.);
}
