float rand(vec2 co) {
    highp float a = 12.9898;
    highp float b = 78.233;
    highp float c = 43758.5453;
    highp float dt= dot(co.xy ,vec2(a,b));
    highp float sn= mod(dt,3.14);
    return fract(sin(sn) * c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    float res = 1.;
    float sunc = cos(iTime*2.+iTime*uv.y/10.)/100.;
    float sun = distance(vec2(1., .8), uv+sunc);
    sun = 1.-smoothstep(sun, .1, .11)+rand(uv)/10.;
    res = sun;


    for (int i = 5; i > 1; i--){
        vec2 st = uv;
        float j = float(i)/10.;
        float h = .1+j;
        float wavec = iTime/10.+sin(iTime*j+h)/10.;
        float c = cos((st.x*st.y*20.)*sin(j*h*33.14+wavec))/20.;
        st.y += c;
        if (uv.y < h+c) {
            float d = distance(uv, vec2(uv.x, h+.05));
            //float d = distance(uv, vec2(uv.x, st.y+h-.05));
            res = d+rand(uv)/10.;
        }
    }

    vec3 color = vec3(res);

    fragColor = vec4(color,1.0);
}
