#define PI atan(1.)*4.
#define R(p,a,r) mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h) cos((h)*6.3+vec3(0,23,21))*.5+.5
#define R2(p,t) p*cos(t)+vec2(p.y,-p.x)*sin(t)
#define Q(p)p=R2(p,round(atan(p.y,p.x)/a)*a),

void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,u,r=iResolution,d=normalize(vec3(C-.5*r.xy,r.y));
    for(float i=0.,e,g=0.,t=iTime,a=PI/3.;
        i++<80.;
        O.xyz+=mix(vec3(1),H(p.y*3.+.5),.5)*.02/exp(.3*i*i*e)
    ){
        p=d*g-i/1e5,
        p.z-=1.8,
        p=R(p,normalize(H(t*.02)-.5),t*.1);
        for(int i=0;i++<7;)
            Q(p.yx)
            p.y-=.6,
            Q(p.zy)
            p.z-=.6;
        g+=e=abs(dot(abs(p),vec3(.577))-.2)+1e-4;
    }
    O*=O;
}
