// функция для рисования кругов
    bool circle(vec2 p, vec2 c, float r)
    {
        float scaleY = iResolution.y / iResolution.x;
         return (p.x - c.x) * (p.x - c.x) + (p.y   - c.y) * (p.y - c.y) * (scaleY * scaleY)  < r*r; 
    }


    // функция для рисования эллипса
    bool elipse(vec2 p, vec2 c, float r, float a, float b)
    {
        float scaleY = iResolution.y / iResolution.x;
        float dx_2 = (p.x - c.x) * (p.x - c.x);
        float dy_2 = (p.y - c.y) * (p.y - c.y);
         return dx_2 / (a*a) + (dy_2 / (b*b)) * (scaleY * scaleY) < r*r;

    }



    // Несколько вспомагательных функций для отображения треугольников:

    // Уравнение прямой
    float calc_line_eq(vec2 a, vec2 b, vec2 p)
    {
        return (p.x - a.x) * (b.y - a.y) - (p.y-a.y) * (b.x - a.x);
    }


        // Проверяет лежат ли точки на одной стороне прямой:
        bool isOnOneSide(vec2 a, vec2 b, vec2 c, vec2 p) 
        {
            return calc_line_eq(a, b, c) * calc_line_eq(a, b, p) >= 0.0;
        }

            // перебор возможных случаев
            bool triangle(vec2 a, vec2 b, vec2 c, vec2 p)
            {
                return  isOnOneSide(a, b, c, p) && isOnOneSide(b, c, a, p) && isOnOneSide(c, a, b, p);
            }




