#define S(r,c) max( 0., ( abs( fract((c)*r/2.) - .5 ) -.5 + width/sharpness ) / fwidth((c)*r/2.)  )

void mainImage( out vec4 O, vec2 u )
{
	vec2 R = iResolution.xy,
         U = ( u - vec2(.5,0) * R) / R.y;               
    U = 4.* vec2( atan(U.x, U.y)- iTime*.07, length(U) );    
    U.x = U.x*.955;

    float sharpness = 100.,
          y = U.y-1.5,
          width = -2.9 + U.y*6. / (1. + (y+sin(iTime*5.)) * y ),
          c1 = U.x + U.y/sqrt(2.),
          c2 = U.x - U.y/sqrt(2.),

          m1 = S(1.,c1) + S(2.,c1) + S(4.,c1),
          m2 = S(1.,c2) + S(2.,c2) + S(4.,c2);

    O = vec4(m1+m2);
    O = sqrt(O);
}
