#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define o(v2,deg) mod((v2)*rot(radians(deg)),60.)-30.;
#define hex() o1=o(v2,0.);o2=o(v2,60.);o3=o(v2,120.);v4+=clamp(vec4(abs(o1.y)+abs(o2.y)+abs(o3.y)-54.),1./16.,1.);
void mainImage(out vec4 v4,in vec2 v2){

    v4=vec4(0,0,0,1);//default black
    vec2 R=iResolution.xy;v2-=R/2.;//center
    vec2 o=v2;//for lighting
    vec2 o1,o2,o3;//for hexagons

    //scale
    v2/=min(R.x,R.y)/360.0;
    o/=min(R.x,R.y)/360.0;

    //position
    v2.x+=sin(iTime)*60.;
    v2.y+=cos(iTime)*30.;
    o.x+=sin(iTime)*60.;
    o.y+=cos(iTime)*60.;
    //rotation
    v2*=rot(iTime);
    //size
    v2/=abs(sin(iTime*1.25))*.75+.25;
    o/=abs(sin(iTime*1.25))*.75+.25;


    if(length(o)<90.0){//if ball
        v2.y+=40.;hex();v2.y+=40.;hex();//hexagons
        v4*=1.0-clamp(vec4(pow(length(o)/90.0,2.0)),0.,1.);//lighting
    }else{//if no ball
        v4+=clamp(1.5-(clamp(60./length(o),0.0,1.0)),0.,1.);//shadow;
        v4*=vec4(1./32.);//background
    }

    v4.w=1.0;//end
}
