void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float muly = uv.y / 3.;

    //left line
    float colg = smoothstep(muly,0.001 + muly,uv.x);
    float invcolg = smoothstep(muly + .001, .002 + muly,uv.x);
    float colf = colg - invcolg;
    //right line
    float rcolg = smoothstep(muly,0.001 + muly,1.-uv.x);
    float rinvcolg = smoothstep(muly + .001, .002 + muly,1.-uv.x);
    float rcolf = rcolg - rinvcolg;
    //dotted line
    float dottedcolg = smoothstep(0.499, 0.5, uv.x);
    float invdcolg = smoothstep(0.5, 0.501, uv.x);
    float invd = dottedcolg - invdcolg;

    float dots = step(sin(uv.y * 30. + (iTime*20.)) + 1., .5);

    float dline = clamp(invd-dots, 0., 1.);


    vec3 col = vec3(0., colf + rcolf + dline, 0.);

    // Output to screen
    fragColor = vec4(col,1.0);
}
