void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 iCoord = ivec2(fragCoord); //set up coords
    vec3 col = vec3(0); //initialize color

    float speed = texelFetch(iChannel0, ivec2(0), 0).z; //get speed from buffer
    if (float(iCoord.y) <= speed){ //if current pixel y is <= mouse speed
        col = vec3(0.2,0.8,0); //set color to green
    }

    fragColor = vec4(col,1.0); // Output to screen
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 iCoord = ivec2(fragCoord); //set up coords
    vec3 col = vec3(0); //initialize color

    if (iCoord == ivec2(0,0)){
        vec2 prevMousePos = texelFetch(iChannel0, ivec2(0), 0).xy; //read out prev pos from buffer
        vec2 mousePos = iMouse.xy; //set the current pos
        float speed = length(mousePos - prevMousePos); //get the mouse speed
        col = vec3(mousePos, speed); //store data in color
    }

    fragColor = vec4(col,1.0); //write the speed and current pos to the buffer
}
