// written by mossan-hoshi( https://twitter.com/mossan_hoshi )

#define K 1000.
#define THRESHOLD


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  // const 
  // normalize position(x,y:.0~1.0)
  vec2 uv= ((fragCoord.xy - .5 * iResolution.xy)/ min(iResolution.x,iResolution.y));

  // circular zone plate
  float value = (1. + cos(K * mod(iTime, 20.) * dot(uv,uv)))* .5;
  if(iMouse.z > 0.){ 
    // binalize on mouse down
    value = step(.5,value);
  }
  fragColor=vec4(value,value,value,1.);
}
