void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 color = texture( iChannel0, fragCoord/iResolution.xy );
    float amt=450.0*(1.0+sin(2.0*iTime))/2.0;
    float amt1=450.0-(450.0*(1.0+sin(iTime))/2.0);
    vec3 redi=vec3((color.r+color.b+color.g)/3.0);

    if(fragCoord.y < amt && fragCoord.x<iResolution.x/5.0){
    color=vec4(redi, 1.0);
    }
    else if(fragCoord.y < amt1 && fragCoord.x>2.0*iResolution.x/5.0 && fragCoord.x<3.0*iResolution.x/5.0){
    color=vec4(redi, 1.0);
    }
    else if(fragCoord.y < amt1+100.0 && fragCoord.x>4.0*iResolution.x/5.0 && fragCoord.x<5.0*iResolution.x/5.0){
    color=vec4(redi, 1.0);
    }

    else if(fragCoord.x < amt && fragCoord.y<iResolution.y/5.0){
    color=vec4(redi, 1.0);
    }
    else if(fragCoord.x < amt1 && fragCoord.y>2.0*iResolution.y/5.0 && fragCoord.y<3.0*iResolution.y/5.0){
    color=vec4(redi, 1.0);
    }
    else if(fragCoord.x < amt1+100.0 && fragCoord.y>4.0*iResolution.y/5.0 && fragCoord.y<5.0*iResolution.y/5.0){
    color=vec4(redi, 1.0);
    }

    fragColor = color;
}
