uint iters = 12u;

vec4 archipelago(uvec2 I) {
    vec4 O = vec4(1);
    bool b = false;
    for(uint i = 0u; i++ < iters;I = i+1u+(I+(I.yx)/uvec2(11u,13u)-I.yx/uvec2(13u,11u))*2u/3u){
        b = !b || bool(I.x & 1u) && bool(I.y & 1u);
        //b = !b || bool(I.x & uint(b)) && bool(I.y & uint(b));

        O.x = abs(float(b)-O.x)/2.;
        O.xyz = abs(float(b)-abs(O.yzx))/2.;
    }
    if (!b) O = 1.-O;
    return O;
}

void mainImage(out vec4 O, vec2 U) {
    uvec2 I = uvec2( U/4.+ iTime*16.);
    O = archipelago(I);
}

float fmod(float a, float b){
    return
        mod(floor(a),b)
    ;
}

vec2 mainSound(int samp, float t){
  float tempo = 1.;
  t /= tempo;
  float
  tempo1 =
      pow(2.,1.+fmod(t*4.,2.))
  ,
  m1 =
      pow(2.,1.+fmod(t/tempo1,2.))
  ,
  c1 = fmod(t*tempo1/2.,m1+fmod(t,m1)),
  s1 = 1.+c1,
  t1 = t*tempo1,
  m2 = fmod(t1*(1.+fmod(t/2.,2.)),s1),
  a=
      5.*fract(-t*tempo/s1)
  ,
  nb =
      7.*t*tempo*pow(2.,m2/5.+7.)/radians(180.)
  ;
  return
      abs(vec2(cos(nb),sin(nb)))*a
  ;
}
