//reference image: https://static.projects.iq.harvard.edu/files/styles/os_files_xlarge/public/science-demonstrations/files/thinfilminterference-freeze-wide-2-640x360.jpg
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 lambda = vec3(612.0f, 535.0f, 465.0f); //primaries' wavelengths
    vec3 omega = uv.x / lambda;
    vec3 col = vec3(cos(omega * 20000.0f) * 0.5f + 0.5f); //20000 is an arbitrary scale constant
    col = mix(vec3(0.25f), col, exp(-omega * 1000.0f)); //smudge the interference pattern towards the right end
    //col.rgb = vec3(col.b);
    fragColor = vec4(pow(col, vec3(1.0f / 2.2f)), 1.0f); //linear->srgb
}
