void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv=(fragCoord.xy/iResolution.xy-0.5)/vec2(iResolution.y/iResolution.x,1); //UV.y from -.5 to .5 and UV.x from -.88 to .88 (aspect ratio broooh)

    // Time varying pixel color
    vec3 col = vec3(uv.x +.5 , uv.y +.5, sin(uv.x * 10. + iTime) * sin(uv.y * 10.) / 2. + .5);

    // Output to screen
    fragColor = vec4(col,1.0);
}
