vec2 N(float angle)
{
return vec2(sin(angle), cos(angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec2 mouse= iMouse.xy/iResolution.xy;
    uv *=1.25;

    vec3 col = vec3(0);
    uv.x = abs(uv.x);
    uv.y += tan((5.0/6.1)*3.1415)*0.5;

    vec2 n = N(5.0/6.0*3.1415);
    float d = dot(uv-vec2(0.5,0),n);
    uv-= n*max(0.0,d)*2.0;


    n = N(abs(sin(iTime*0.13))*(2.0/3.0)*3.1415);

    float scale =1.0;
    uv.x+=0.5;
    for(int i = 0;i<25;i++)
    {
        uv*=3.0;
        scale*=2.95;
        uv.x-=1.5;

        uv.x = abs(uv.x);
        uv.x -= 0.5;
        uv-= n*min(0.0,dot(uv,n))*2.0;
    }


    d = length(uv - vec2(clamp(uv.x, -1., 1.),1));
    col += smoothstep(1.0/iResolution.y,0.0,d/scale);
    uv/=scale;
    col+= texture(iChannel0,uv*3.-iTime*0.15).rgb;

    fragColor = vec4(col,1.0);
}
