float PI = 3.14159;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 c = vec2(0.5, 0.5);
    vec2 diff = ( fragCoord -.5* iResolution.xy ) /iResolution.y;;
    float TAU = 2. * PI;
    float th = atan(diff.y, diff.x);
    float ft = 0.1 + 10. * 0.5 * (1. + sin(iTime)) * length(diff);
    float squig = sin(iTime * 0.3) * 10.;
    float freq = 10. * sin(iTime * 0.2) + sin(length(diff) * TAU * squig);
    vec3 col = vec3(pow(0.5 * (1. + sin(TAU * length(diff) * freq + iTime * 3. + th * 3.)), ft));

    fragColor = vec4(col,1.0);
}
