float interval=3.;//时间间隔
float noiseIntensity=0.1;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    //写得有点奇怪
    float strength = smoothstep(interval * 0.5, interval, interval - mod(iTime, interval));

    float waveNoise = (sin(fragCoord.y) + 1.0) * (0.15 + strength * 0.2);

    vec4 res = texture(iChannel0, uv);  
    fragColor = vec4(res.rgb, 1.0) + (waveNoise) * noiseIntensity;
}
