void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0) * aa(loop(iTime + uv.x - 0.5));

    if(uv.x >= 0.499 && uv.x <= 0.501)
        fragColor = vec4(1., 1., 1., 2.) - fragColor;
}

vec2 mainSound( int samp, float time )
{
    float time2 = loop(time);
    float t = time / 0.004 * 2. * PI;
    float a = sin(t);
    a *= aa(time2);
    return vec2( a );
}

#define PI 3.141592653589

float loop(float time)
{
    float baseTime = time;
    // time = pow(time + 1., 1.2) - 1.;
    if(time < 0.)
        return 0.;

    /*float p1 = 2. - (0.6 + 0.6 * tanh(time * 0.2 - 10.));
    time /= p1;
    time = fract(time);
    time *= p1;
    */
    /*time = log(fract(1. + exp(time * 0.1)));
    time /= 0.1;
    time *= 4.;
    */
    const float Q = 6.;
    const float M = 0.1;
    time = (time + Q) * M - log(floor(exp((time + Q) * M)));
    time /= M;

    time = time / 0.2;
    if(time >= 4.)
        return 0.;
    time = fract(time);
    time = time * 0.2;
    return time;
}

float aa(float time)
{
    if(time <= 0.)
        return 0.;
    float t = time / 0.004 * 2. * PI;
    float t2 = time / 0.040 * 2. * PI;
    float a = 1.;
    a *= 0.7 + 0.3 * (1. - cos(t2));
    a *= pow((0.2 - time) / 0.2, 2.);
    if(time <= 0.01) a *= time / 0.01;
    return a;
}
