int itterations = 10;
float upperBound =100000.;

float st(float m){
return m*sin(iTime);
}

float polynomial(float x, float n5,float n4,float n3,float a,float b,float c){
	return ((n4*x*x*x*x)+(n3*x*x*x)+(x*a*x)+(b*x)+c);
}
float f(float x,float modifier){

   vec2 mouseUv = 10.*(vec2(0.5)-((iMouse.xy/iResolution.xy)));
        return polynomial(x,0.,0.,mouseUv.x,modifier,1.,mouseUv.y);


}


vec2 cobweb(float x0,float modifier){

    float result = f(x0,modifier);
    float maxI;
    for(int i=0;i<itterations;++i)
    {
        if(abs(result)>upperBound){

        }else{
            result = f(result,modifier);
            maxI = float(i);
        }
    }

    return vec2(result,maxI);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy-vec2(0.5);

    // Time varying pixel color
    vec3 col = vec3(0.2,0.1,0.4);//0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

        vec2 mouseUv = 0.1*(vec2(0.5)-((iMouse.xy/iResolution.xy)));
        mouseUv=vec2(0);

    //float scale = 0.0005;
    float scale = 20.;



    vec2 offset = mouseUv ;
    uv = (uv+offset)*scale + vec2(0.000,2.0);

    if(iMouse.w>0.5){
       if(abs(f(uv.x,5.*sin(iTime))-uv.y)<scale*0.01){
            col = vec3(1);
        } 
    }else{
      float shade = abs(cobweb(uv.x,uv.y)).y/float(itterations);
         col = vec3(shade);
         if(shade==1.0){
         col= vec3(0);
        }
    }

    //col.xy = mouseUv.xy;




    // Output to screen
    fragColor = vec4(col,1.0);
}
