void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.x/iResolution.x, fragCoord.y/iResolution.y);
    vec2 mouse = vec2(iMouse.x / iResolution.x, iMouse.y / iResolution.y);

    vec4 col = texture(iChannel0, uv);

    float d = sqrt(pow(uv.x - mouse.x, 2.0) + pow(uv.y - mouse.y, 2.0));

    int blurr = int(d * 25.0);
    vec2 newUV = vec2(0.0, 0.0);
    vec4 newCol = vec4(0.0, 0.0, 0.0, 0.0);
    float r_all = 0.0;
    float g_all = 0.0;
    float b_all = 0.0;

    for(int row = -blurr; row <= blurr; row++){
        for(int col = -blurr; col <= blurr; col++){
            newUV = vec2((fragCoord.x + float(col))/iResolution.x, (fragCoord.y + float(row))/iResolution.y);
            newCol = texture(iChannel0, newUV);
            r_all += newCol.x;
            g_all += newCol.y;
            b_all += newCol.z;
        }
    }

    float divider = pow(float(blurr) * 2.0 + 1.0, 2.0);

    fragColor = vec4(r_all / divider, g_all / divider, b_all / divider, col.w);
}
