void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float flare = abs(uv.x - 0.5)*0.5;
    flare += smoothstep(0.0,0.15,abs(uv.y - .5));


    vec3 skyblue = vec3(84./255.,208./255.,227./255.);
    vec3 white   = vec3( 255./255.,  255./255., 255./255. );
    vec3 black      = vec3( 0., 0., 0. );
    float t = pow(1.-0.7*flare, 15.0);


    vec3 col = (white*t + black*(1.0-t));

    col += smoothstep(0.,2.,col);

    fragColor = vec4(col,col.r);
    fragColor.rgb *= skyblue;




}
