// Filip's first custom shader.
//
// [i-have-no-idea-what-i'm-doing.gif]
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Distance from center.
    float toCenter = distance(iResolution / 2.0, fragCoord);

    // Make the circle around the center.
    float dist = pow(
        1.0 - (toCenter + sin(iTime) * 50.0) / 300.0, 
        0.1);
    vec3 col = vec3(max(dist, 0.0));

    // Make it red when clicked.
    if (iMouse.z > 0.0) {
      col += vec3(-.2, -1.5, -1.5);
    }

    // Add the ripples.
    if (col.x < 0.1) {
      vec2 rippled = vec2(fragCoord.x - cos(iTime + fragCoord.x / 12.0) * 0.8, fragCoord.y + sin(-iTime + fragCoord.x / 10.0) * 1.2 );
      toCenter = distance(vec2(iResolution / 2.0), rippled);
      float wave = 0.5 + 0.5 * sin(-5.0*iTime + toCenter / 12.0);
      col = (toCenter / iResolution.x / 2.0) 
               * vec3(pow(smoothstep(0.4, 0.8, wave), 1.));
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}
