vec2 offset = vec2(-0.545, 0.498);
float zoom = 295.0;

#define cmul(A,B) ( mat2( A, -(A).y, (A).x ) * (B) ) // https://shadertoyunofficial.wordpress.com/2019/01/02/programming-tricks-in-shadertoy-glsl/
#define mod(A,B) ( (A) - ((B) * floor((A)/(B))) )

vec2 normalizedCoord(vec2 fragCoord) {
    vec2 norm = fragCoord/iResolution.xy * 2.0 - 1.0; // normalize to (-1, 1) range
    norm.x *= iResolution.x/iResolution.y; // normalize to square aspect ratio
    return norm;
}


vec3 hsl2rgb( in vec3 c ) { // Â© 2014 Inigo Quilez, MIT license, see https://www.shadertoy.com/view/lsS3Wc
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

// https://www.linas.org/art-gallery/escape/smooth.html
float smoothCount(float escapeCount, vec2 escapeValue) {
    return escapeCount - log2(log(length(escapeValue)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) { 
    float iterations = 160.0 - 130.0 * cos(iTime/5.0);

    vec2 z = vec2(0, 0);
    vec2 c = normalizedCoord(fragCoord)/zoom + offset;

    float i = 0.0;    
    while (i < iterations) {
        z = cmul(z, z) + c;
        if (dot(z,z) > 8.0) break;
        i++;
    }

    if (i >= iterations) {
        fragColor = vec4(0,0,0,1);
    } else {
        float hue = -smoothCount(i,z)/iterations;
        vec3 hsl = vec3(
            mod(hue + iTime/15.0, 1.0), 
            0.9, 
            0.3
        );
        fragColor = vec4(hsl2rgb(hsl), 1);
    }
}
