float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec2 winUV = fragCoord/iResolution.xy;
    vec2 UV = fract(uv*20.);

    float variation = smoothstep(0.95,1.0,  sin(iTime*3.-length(uv) * 5.)*.5+.5)*.5+.25;

    float circle = sdCircle(UV-0.5, 0.2 * variation);
    circle = smoothstep(0.1,0.0, circle);

    circle = mix(circle,.5, .3); //Lower contrast

    vec3 col = vec3(circle);
    col *= vec3(.6,.8,.4);

    // Output to screen
    fragColor = vec4(col,1.0);
}
