float rand(float x) { return fract(sin(x)*100000.0); }
#define M_PI 3.1415926535897932384626433832795


float circle(float i, vec2 p) {
    float a = rand(i);
    vec2 p1 = vec2(rand(i+562.0), rand(i+761.0));
    vec2 pv1 = vec2(sin(a * 2.0 * M_PI), cos(a * 2.0 * M_PI)) / 35.0;

    float r = abs(sin(iTime * 0.2 + rand(i + 345.0) * 28.0)) * 0.5 + 0.4;
    vec2 px = p1 + pv1 * iTime*4.0;

    return clamp(r - length((mod(px, 2.0) -1.0) * 5.0 + p), 0.0, r) / r;
}


vec2 map(vec2 p) {

    float l = 0.0;
    float m = 0.0;
    float ix = 0.0;

    for(int i = 0; i < 80; ++i) {
        float t = circle(float(i), p * (sin(iTime) * 0.5 + 2.0));
        l += t * t;
        if(t > m) {
            m = t;
            ix = float(i);
        }
    }

    return vec2(l, ix);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( 2.*fragCoord - iResolution.xy ) / iResolution.y;
    vec2 o = map(uv + sin(uv.x * iTime/300.0)/50.0);

    o.x = clamp(o.x, 0.0, 0.92);
    o.x = 1.2 - step(fract(o.x * 5.2), 0.9) / floor(o.x * 5.0);


    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    //vec3 col = vec3(rand(o.y) + 0.5,rand(o.y*2.0) + 0.5, rand(o.y*3.0) + 0.5)/2.0; 
    col *= o.x;

    // Output to screen
    fragColor = vec4(col, 1.0);
}
