float hline(float y, float x, float slope,float offset)
{
    float ymp = (y + slope - .5 + offset);
    float sqr = 1. - pow(ymp + x * .5 , 2.) * 1000.;
    return sqr;
}

float clamp01(float num)
{
    return clamp(num, 0., 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float line1 = clamp01(hline(1.-uv.y, uv.x, .5 * uv.x * (2. + sin(iTime + .5)*2.), -.5));
    float line2 = clamp01(hline(1.-uv.y, uv.x, .5 * uv.x * (2. + sin(iTime + 1.)*2.), -.5));
    float line3 = clamp01(hline(1.-uv.y, uv.x, .5 * uv.x * (2. + sin(iTime + 1.5)*2.), -.5));
    float line4 = clamp01(hline(1.-uv.y, uv.x, .5 * uv.x * (2. + sin(iTime + 2.)*2.), -.5));
    float line5 = clamp01(hline(1.-uv.y, uv.x, .5 * uv.x * (2. + sin(iTime + 2.5)*2.), -.5));
    float line6 = clamp01(hline(1.-uv.y, uv.x, .5 * uv.x * (2. + sin(iTime + 3.)*2.), -.5));
    float line7 = clamp01(hline(1.-uv.y, uv.x, .5 * uv.x * (2. + sin(iTime + 3.5)*2.), -.5));
    float line8 = clamp01(hline(1.-uv.y, uv.x, .5 * uv.x * (2. + sin(iTime + 4.)*2.), -.5));
    float line9 = clamp01(hline(1.-uv.y, uv.x, .5 * uv.x * (2. + sin(iTime + 4.5)*2.), -.5));
    float line10 = clamp01(hline(1.-uv.y, uv.x, .5 * uv.x * (2. + sin(iTime + 5.)*2.), -.5));
    float line11 = clamp01(hline(1.-uv.y, uv.x, .5 * uv.x * (2. + sin(iTime + 5.5)*2.), -.5));
    float line12 = clamp01(hline(1.-uv.y, uv.x, .5 * uv.x * (2. + sin(iTime + 6.)*2.), -.5));

    float liner = line1+line4+line7+line10;
    float lineg = line2+line5+line8+line11;
    float lineb = line3+line6+line9+line12;




    // Output to screen
    fragColor = vec4(liner, lineg, lineb,1.0);
}
