/* src smin:
https://inspirnathan.com/posts/51-shadertoy-tutorial-part-5
*/


vec4 smin(float a, float b, float k) {
  vec3 col_a = vec3(1.f, 1.f, 0.f);
  vec3 col_b = vec3(0.f, 1.f, 1.f);

  float h = clamp(0.5+(0.5*(b-a)/k), 0.0, 1.0);
  float dist = mix(b, a, h) - k*h*(1.0-h);

  return vec4(dist, mix(col_b, col_a, h));
}

float sdfCircle(vec2 uv, float r, vec2 offset){
    float x = uv.x - offset.x;
    float y = uv.y - offset.y;

    return length(vec2(x, y)) - r;
}


vec3 drawScene(vec2 uv){
    vec3 col = vec3(0.8, 0.8, 0.8);
    vec3 circlecol = vec3(1., 1., 0.);

    //circle 1
    float rotspeed = 1., rayonrot = 0.3;
    vec2 traveling = vec2(sin(iTime*0.3)*0.3, 0);
    vec2 poscircle = vec2(  traveling.x+sin(iTime*rotspeed)*rayonrot, 
                            traveling.y+cos(iTime*rotspeed)*rayonrot);


    float d1 = sdfCircle(uv, 0.1, poscircle);
    float d2 = sdfCircle(uv, 0.2, vec2(0.,0.));

    vec4 res_smin = smin(d1, d2, 0.1); 

    col = mix(res_smin.yzw, col, step(0., res_smin.x));

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = drawScene(uv);

    // Output to screen
    fragColor = vec4(col,1.0);
}
