void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 mouse = (iMouse.z == 0.0 ? iResolution.xy / 2.0 : iMouse.xy) / iResolution.xy;
    float dispFactor = mouse.x;
    float intensity = mouse.y;
    fragColor = mix(texture(iChannel0, vec2(uv.x, uv.y + dispFactor * (texture(iChannel1, uv).r * intensity))), texture(iChannel1, vec2(uv.x, uv.y + (1.0 - dispFactor) * (texture(iChannel0, uv).r * intensity))), dispFactor);
}
