precision highp float;
float randpos( vec2 pos){
    return fract(
        sin(15.*pos.x)*52.392*15.*(0.2+pos.y) +
        sin(345.*pos.y)*165.+6.*pos.x +
        sqrt(abs(pos.x*pos.y)) + 
        15.546 + pos.x + pos.y 
        );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 c = fragCoord.xy - (iResolution.xy *0.5 + 20.* vec2(sin(0.8*iTime), 0.35*cos(iTime)));
    c = 1.*ceil(c/5.);
    float dist = sqrt( dot(c,c) );
    float ang = atan( c.y , c.x );
     float rand = randpos(c);
     float result = ceil((sin(ang * 10. + iTime)-rand));
     result *= min(1.,iResolution.y/(20.*dist));
fragColor = vec4( result, 0., 0., 1.0);
}
