vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv *= 5.0;

    vec2 p1 = vec2(sin(iTime*0.6942),cos(iTime*0.4269));
    vec2 p2 = vec2(cos(iTime*0.888+0.8989),sin(iTime*0.666+69.69));
    vec2 p3 = vec2(cos(iTime*1.069428+69.8989),sin(iTime*0.96942+55.88));


    float d1 = length(uv-p1);
    float d2 = length(uv-p2);
    float d3 = length(uv-p3);

    d1 = sin(d1+iTime*0.9);
    d2 = sin(d2+iTime*0.8);
    d3 = sin(d3 + iTime*0.87);

    float d = min(d1,d2);
    d = min(d,d3);



    vec3 col = vec3(0);

    col = palette(
    d*0.5,
    vec3(0.5),
    vec3(.5),
    vec3(1.),
    vec3(.0,.1,.2));
    col = mix(col, palette(d*0.5,vec3(.5),vec3(.5),vec3(1.),vec3(.0,.33,.66)), d2 *0.5+0.5);
    col = mix(col, palette(d*0.5,vec3(.5),vec3(.5),vec3(1.),vec3(.0,.0,.0)), d1*0.5+0.5);

    fragColor = vec4(col,1.0);
}
