#define F(x,y) c = mix(x, c, smoothstep(0., fwidth(y), y))
#define r iResolution

float sdArc( in vec2 p, in vec2 sca, in vec2 scb, in float ra, float rb )
{
    p *= mat2(sca.x,sca.y,-sca.y,sca.x);
    p.x = abs(p.x);
    float k = (scb.y*p.x>scb.x*p.y) ? dot(p,scb) : length(p);
    return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/r.xy- .5;
    uv.x *= r.x/r.y;
    uv.x = abs(uv.x);

    uv *= 0.1 *  pow(1. + (length(uv) - .15), 16.);

    vec3 c = vec3(54, 57, 62)/256.;
    F(vec3(1, 0.8, 0.302), length(uv)-.5);

    F(vec3(1, 0.4706, 0.5725), length(uv-vec2(0.3,-0.1))-.15);
    F(vec3(0.9608, 0.9725, 0.9804), length(uv-vec2(0.18,0.04))-.15);
    F(vec3(0.4, 0.2706, 0), length(uv-vec2(0.18,0.04))-.08);

    F(vec3(0.4, 0.2706, 0), sdSegment(uv-vec2(0.1, -0.28),vec2(-0.1,-0.0),vec2(0)) - .03);

    float ta = 8.4;
    F(vec3(0.4, 0.2706, 0), sdArc(uv+vec2(-0.1,0), vec2(sin(ta),cos(ta)), vec2(sin(.3),cos(.3)), .35, .03));

    fragColor = vec4(c,1.0);
}
