#define MapSize 10




float random(float x) {
     return fract(sin(x * 457.87 + 0.001  ) * 10000.0);
}




float value_noise_1D(float u)

{
    // Fractial part.
    float f = fract(u);

    // Integer part.
    float i = floor(u);

    float y1 = random(i);
    float y2 = random(i + 1.0);

    float res = mix(y1, y2, smoothstep(0.0 , 1.0 , f) );
    return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy ;



    float _u = uv.x* float(MapSize) ;

    float y = value_noise_1D(_u)  ;
   // float y = value_noise_1D_map(_u)  ;



    float lineWidth =  0.01 ;

    if(uv.y>y && uv.y<y+ lineWidth){
        fragColor = vec4(0.0,0.0,0.0,1.0);
    }else{
        fragColor = vec4(1.0,1.0,1.0,1.0);
    }   


}
