    vec3 skytop= vec3(0.459,0.749,1.05);
    vec3 skybottom = vec3(1.1,0.533,0.000);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;    
     vec3 col = mix(skybottom,skytop,sqrt(uv.y));
     col *= col * 1.2 + .35;
    fragColor = vec4(col,1.0);
}
