#define r(a) (mat2(cos(a),sin(a),-sin(a),cos(a)))
void mainImage(out vec4 e, in vec2 v) {
    vec3 d=vec3((v.xy-iResolution.xy*.5)/iResolution.y,.4),p;
    d.yz*=r(iTime*.2); d.xz*=r(iTime*.2);
    for(float o,i=0.;i<33.;i++){
        o=.2-length(cos(p.xy)+sin(p.yz-vec2(-1.,iTime*5.)));
        p+=(o-.1-(sin(iTime)+1.)*.3)*d;
        e.xyz+=vec3(.2,.8,.9)*(.005/clamp(abs(o),.01,9.));
    }
}
