void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ( 2.*fragCoord -iResolution.xy ) / iResolution.y;

    fragColor.rg = uv;
    fragColor.b = 1.0;

    float thickness = iMouse.x/iResolution.x;
    float fade = 0.005;
    thickness += fade;
    float wfreq = 50.0;
    float distance = 1.0 -length(uv);
    vec3 col = vec3(smoothstep(0.0,fade,distance));

    //fragColor.rgb = vec3(distance);
    //fragColor.rgb = vec3(1.0,0.50,0.4);
    col*= vec3(smoothstep(thickness,thickness-fade,distance));



    fragColor.rgb = col;
    fragColor.rgb *= cos(iMouse.x/iResolution.x+iTime+uv.xyx+vec3(0,2,4));//

}
