float[9] MAT= float[9](-1.0, -1.0, -1.0, -1.0, 8.0, -1.0, -1.0, -1.0, -1.0);
float BBM=0.9;
float BIAS=0.1;
//
//
//
//
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color

    vec2 pp=(fragCoord+1.)/iResolution.xy;
    vec2 pc=uv;
    vec2 pm=(fragCoord-1.)/iResolution.xy;
    vec4[9] m; 
    m[0]=texture(iChannel0,pm);
    m[1]=texture(iChannel0,vec2(pc[0],pm[1]));
    m[2]=texture(iChannel0,vec2(pp[0],pm[1]));
    m[3]=texture(iChannel0,vec2(pm[0],pc[1]));
    m[4]=texture(iChannel0,pc);
    m[5]=texture(iChannel0,vec2(pp[0],pc[1]));
    m[6]=texture(iChannel0,vec2(pm[0],pp[1]));
    m[7]=texture(iChannel0,vec2(pc[0],pp[1]));
    m[8]=texture(iChannel0,pp);
    int i,j;
    vec3 color=vec3(0.,0.,0.);
    for(i=0;i<9;i++){
        for(j=0;j<3;j++){

            color[j]+=m[i][j]*MAT[i];
        }
    }
    color=color*BBM+BIAS;
    // Output to screen
    fragColor = vec4(color[0],color[1],color[2],1.0);
    //fragColor= vec4(1.0,0.0,0.,1.0);
}
